let finalResult,num1=document.getElementById("firstNum"),num2=document.getElementById("secNum"),operator=document.getElementById("operation"),btn=document.getElementById("btncalculate"),result=document.getElementById("result");btn.addEventListener("click",function(){switch(num1=parseInt(num1.value),num2=parseInt(num2.value),console.log(typeof num1),operator.value){case"add":finalResult=num1+num2;break;case"minus":finalResult=num1-num2;break;case"multiply":finalResult=num1*num2;break;case"division":finalResult=num1/num2;break;case"default":console.log("Operation error")}result.innerHTML=finalResult});